var pokeData = require('../lib/pokemon.json');

module.exports = function (input) {
  if (!input) {
    return pokeData;
  }

  var pokemon = pokeData.find(function (pokemon) {
    if (Number.isInteger(input)) {
      return pokemon.species_id == input;
    } else {
      return pokemon.name === input;
    }
  });
  return pokemon || new Error("".concat(input, " was not found in the Pokedex."));
};
