import React, { Component } from "react";

export default class About extends Component {

    render() {
    return (
    <div>
        <h1 className="display-1">About Us</h1>
        <div className="container">
            <div className="row">
                <div className="col-md-9">
                    <div className="media">
                        <img className="align-self-start mr-3" src="./assets/images/company-event-ideas-to-boost-morale.jpg" alt="my_image" />
                        <div className="media-body">
                            <p>hALP was created to thelp serve those who are in search for restaurants serving authentice Halal food. We take pride in providing value to our end users by offering a simple and friendly search process. </p>
                            <p>Our mission is to take the pain out of using traditional food apps that renders all results and then having the user proceed to verify if the restaurant serves Halal or not.</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    </div>    

  );
}
}
